<?phpnamespace Bfg\BlessModel\ApplyLevy;use Bfg\BlessModel\Facades\BlessModel;use Bfg\BlessModel\LevyModel;use Bfg\BlessModel\LevyModelCollection;/** * Class SaveLevyModelCollectionListener * @package Bfg\BlessModel\ApplyLevy */class SaveLevyModelCollectionListener{    /**     * @param  LevyModelCollection  $model     * @return LevyModelCollection     */    public function handle(LevyModelCollection $model): LevyModelCollection    {        return $model->map(fn (LevyModel $model) => BlessModel::applyLevy($model));    }}